import React, {useEffect, useState} from 'react';
import { useHistory, useParams } from 'react-router-dom';
import styled from 'styled-components';
import './Detail.scss';

let 박스 = styled.div`
  padding : 20px;
`;

let 제목 = styled.h4`
  font-size : 25px;
`;

/*
class Datail2 extend React.Component {
  // Detail2 컴포넌트가 Mount(등장) 되었을 때 실행할 코드
  componentDidMount() {

  }
  // Detail2 컴포넌트가 Unmount(사라짐) 되었을 때 실행할 코드
  componentWillUnmount() {

  }
}
*/


function Detail(props) {
  // 컴포넌트가 mount 되었을 때, 컴포넌트가 update 될 때 특정 코드 실행할 수 있음
  useEffect(()=>{
    console.log(111)
  })

    let { id } = useParams(); // 사용자가 입력한 URL 파라미터들
    let history = useHistory();
    let 찾은상품 = props.shoes.find(x => x.id == id);

    return (
      <div className="container">
        <박스> 
          <제목 className="red">Detail</제목>
        </박스>

        <div className="my-alert my-alert2">
          <p>재고가 얼마 남지 않았습니다.</p>
        </div>

        <div className="row">
          <div className="col-md-6">
            <img src={"https://codingapple1.github.io/shop/shoes" + (찾은상품.id+1) + ".jpg"} width="100%" />
          </div>
          <div className="col-md-6 mt-4">
            <h4 className="pt-5">{찾은상품.title}</h4>
            <p>{찾은상품.content}</p>
            <p>{찾은상품.price}</p>
            <button className="btn btn-danger">주문하기</button> 
            <button className="btn btn-danger" onClick={()=>{ history.goBack(); }}>뒤로가기</button> 
          </div>
        </div>
      </div> 
    )
}

export default Detail;
